<style lang="scss">
@import "@/Shared/Assets/Scss/container";
@import "@/Shared/Assets/Scss/vars";
@import "@/Containers/Index/UI/Assets/Scss/index";
@import "@/Shared/Assets/Scss/grid";
@import "@/Shared/Assets/Scss/indention";
@import "@/Shared/Assets/Scss/fonts";

.index-clients {
    position: relative;
    padding-bottom: 120px;
    &__background {
        filter: brightness(0.2);
        position: absolute;
        z-index: 0;
        width: 100%;
        left: 0;
        top: 0;
    }
    &__container {
        position: relative;
        z-index: 1;
        gap: 100px;
        @include grid-row;
        @include section-padding;
        @include site-container;
    }
    &__statistic-item {
        text-align: center;
        display: grid;
        grid-column: 3 span;
    }
    &__statistic-icon {
        justify-content: center;
    }
    &__statistic-name {
        color: $white-color;
        @include margin-vertical(12px);
        @include helvetica-24-bold;
    }
    &__statistic-description {
        color: $white-color;
        align-self: end;
        @include helvetica-40-bold;
    }
}
</style>